import React, { useState } from 'react'

export default function TextForm(props) {

    const clear = () => {
        const textArea = document.getElementById('textArea')
        textArea.value = ''
        
        
    }

    const toUpper = () => {
        let newText = text.toUpperCase()
        setText(newText)

    }
    const toLower = () => {
        let newText = text.toLowerCase()
        setText(newText)

    }

    const onChangeText = (event) => {
        setText(event.target.value)
    }
    const [text, setText] = useState("Enter your Text");
    return (
        <>

            <div>
                <h1>{props.heading}</h1>
                <div className="mb-3">
                    <textarea className="form-control" value={text} onChange={onChangeText} id="textArea" rows="10"></textarea>
                </div>
                <button className="btn btn-primary" onClick={toUpper}>Conver to Uppercase</button>
                <button className="btn btn-primary mx-3" onClick={toLower}>Conver to Lower Case</button>
                <button className="btn btn-primary mx-3" onClick={clear}>Clear Text Area</button>
            </div>

            <div className="container my-3">
                <h1>Your Text Summary</h1>
                <p> {text.split(' ').length} Words and {text.length} Charcters </p>
                <p> {0.008 * text.split(' ').length} minutes read </p>
            </div>
        </>
    )
}
