import React from 'react'

export default function About() {
  return (
    <div style={containerStyle}>
      <h2 style={headerStyle}>Introducing Text Utils - Simplify Your Text Tasks!</h2>
      <p style={paragraphStyle}>
        Text Utils is your go-to tool for quick and easy text manipulations. With just a few clicks,
        you can convert text to lower case, upper case, and extract emails from any text.
      </p>
      <h3 style={headerStyle}>Features:</h3>
      <ul>
        <li style={listItemStyle}>
          <strong>Convert to Lower Case:</strong> Transform your text to all lower case letters effortlessly.
          Perfect for standardizing text or preparing data for analysis.
        </li>
        <li style={listItemStyle}>
          <strong>Convert to Upper Case:</strong> Change your text to all upper case letters with a simple click.
          Great for emphasizing text or creating headlines.
        </li>
        <li style={listItemStyle}>
          <strong>Extract Email:</strong> Extract email addresses from any text automatically.
          Save time and ensure you capture important contact information.
        </li>
      </ul>
      <p style={paragraphStyle}>
        Our intuitive interface makes text manipulation a breeze. Just paste your text, select the desired operation,
        and watch the magic happen!
      </p>
      <p style={paragraphStyle}>
        Text Utils respects your privacy and doesn't store any of your data. It's fast, secure, and accessible from any device.
      </p>
      <p style={paragraphStyle}>
        Simplify your text tasks today with Text Utils. Start converting, extracting, and optimizing your text effortlessly.
        Try it now!
      </p>
    </div>
  );
};

// Styles
const containerStyle = {
  maxWidth: '800px',
  margin: '0 auto',
  padding: '20px',
  fontFamily: 'Arial, sans-serif',
  color: '#333',
};

const headerStyle = {
  fontSize: '28px',
  fontWeight: 'bold',
  marginBottom: '10px',
};

const paragraphStyle = {
  fontSize: '16px',
  lineHeight: '1.6',
  marginBottom: '15px',
};

const listItemStyle = {
  fontSize: '16px',
  lineHeight: '1.6',
  marginBottom: '10px',
};
