import React, { useState } from 'react'

export default function TextForm(props) {
    
    const toUpper = () => {
        let newText = text.toUpperCase()
        setText(newText)
        countWordsandChar()
    }
    const toLower = () => {
        let newText = text.toLowerCase()
        setText(newText)
        countWordsandChar()
    }

    const onChangeText = (event) => {
        setText(event.target.value)
    }
    let [text, setText] = useState("Enter your Text");
    return (
        <>

            <div>
                <h1>{props.heading}</h1>
                <div className="mb-3">
                    <textarea className="form-control" value={text} onChange={onChangeText} id="exampleFormControlTextarea1" rows="10"></textarea>
                </div>
                <button className="btn btn-primary" onClick={toUpper}>Conver to Uppercase</button>
                <button className="btn btn-primary mx-3" onClick={toLower}>Conver to Lower Case</button>
            </div>

            <div className="container">
                <h1>Your Text Summary</h1>
                <p> {words} Words and {charcters} Charcters  </p>
            </div>
        </>
    )
}
